import React, { Component } from 'react'
import jwt_decode from 'jwt-decode'
import axios from 'axios'
import DataGrid, {
  Column,
  Editing,
  Popup,
  Paging,
  Lookup,
  Position,
  Form
} from 'devextreme-react/data-grid';
import 'devextreme-react/text-area';
import { Item } from 'devextreme-react/form';


class ShowTickets extends Component {
  constructor() {
    super()
    this.state = {
     data:[]
    }
  }

  getTicket = test => {
    return axios
      .get('tickets/deneme', {
        
      })
      .then(response => {
        console.log("axios girdi")
        this.setState({
            data:response.data,
            
        })
        console.log("data geldi")
        console.log(response.data)
        return response.data
        
      })
      .catch(err => {
        console.log(err)
      })
  }

 
 
    


  componentDidMount() {
    console.log("componentde")
    const token = localStorage.usertoken
    const decoded = jwt_decode(token)

    this.getTicket()
    console.log(decoded.role)
    this.setState({
        role: decoded.role,
      })

    console.log(this.state.role)
    
  }
 

  render() {

    return (
      <div className="container">
           {this.state.role === 1 ?
        <div className="jumbotron mt-5">
          <div className="col-sm-8 mx-auto">
            <h1 className="text-center">TICKETS</h1>
          </div>
      <div id="data-grid-demo">
        <DataGrid
          dataSource={this.state.data}
          keyExpr="_id"
          showBorders={true}
        >
          <Paging enabled={false} />
          <Editing 
            mode="popup"
            allowUpdating={true}
            >
              <Button
                type="submit"
                className="btn btn-lg btn-primary btn-block"
              >
                Register!
              </Button>
            <Popup title="Ticket Info" showTitle={true} width={700} height={525}>
              <Position my="top" at="top" of={window} />
            </Popup>
            <Form onRowUpdated={(a)=>console.log(a)}>
              <Item itemType="group" colCount={2} colSpan={2}>
                <Item dataField="name" />
                <Item dataField="content" />
                <Item dataField="img" />
                <Item dataField="role" />
                <Item dataField="messages.text" />
                <Item dataField="status" />
                
                <Item
                  dataField="Notes"
                  editorType="dxTextArea"
                  colSpan={2}
                  editorOptions={{ height: 100 }} />
              </Item>

              <Item itemType="group" caption="Home Address" colCount={2} colSpan={2}>
                <Item dataField="StateID" />
                <Item dataField="Address" />
              </Item>
            </Form  >
            
          </Editing>
          
          <Column dataField="Prefix" caption="Title" width={70} />
          <Column dataField="name" />
          <Column dataField="content" />
          <Column dataField="BirthDate" dataType="date" />
          <Column dataField="img" width={170} />
          <Column dataField="HireDate" dataType="date" />
          <Column dataField="StateID" caption="State" width={125}>
            
          </Column>
          <Column dataField="Address" visible={false} />
          <Column dataField="Notes" visible={false} />
          
        </DataGrid>
      </div>
      </div> :<div><h1>User Page</h1></div>
        
        }
      </div>
    )
  }
}

export default ShowTickets
