import { ip } from "../services";

import React, { Component } from "react";
import axios from "axios";
import { getUsers } from "./UserFunctions";


class Ticket extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      content: "",
      messages: [],
      status: "",
      img: "",
      file: "",
      imagePreviewUrl: "",
      currentUser: "", 
      createdBy: "",
      assignToUsers: [], 
      errors: {},
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this._handleImageChange = this._handleImageChange.bind(this);
  }

  componentDidMount() {
    // Fetch users when the component mounts
    getUsers().then(users => {
      this.setState({ users });
    }).catch(error => {
      console.error('Error fetching users:', error);
    });
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  _handleImageChange(e) {
    e.preventDefault();

    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      this.setState({
        file: file,
        imagePreviewUrl: reader.result,
      });
    };

    reader.readAsDataURL(file);
  }

  onSubmit(e) {
    e.preventDefault();

    const formData = new FormData();
    formData.append("img", this.state.file);
    formData.append("name", this.state.name);
    formData.append("content", this.state.content);
    formData.append("messages", JSON.stringify(this.state.messages));
    formData.append("currentUser", this.state.currentUser);
    formData.append("createdBy", this.state.createdBy);
    formData.append("assignToUsers", JSON.stringify(this.state.assignToUsers));

    axios({
      method: "post",
      url: `${ip}/tickets/addTickets`,
      data: formData,
      headers: { "Content-Type": "multipart/form-data" },
    })
      .then((response) => {
        console.log(response);
        // Handle success, e.g., redirect or clear form
      })
      .catch((error) => {
        console.error(error);
        // Handle error, e.g., show error message
      });
  }

  render() {
    const { imagePreviewUrl } = this.state;
    let $imagePreview = null;
    if (imagePreviewUrl) {
      $imagePreview = <img src={imagePreviewUrl} alt="Image Preview" />;
    } else {
      $imagePreview = (
        <div className="previewText">Please select an Image for Preview</div>
      );
    }

    return (
      <div className="container">
        <div className="jumbotron mt-5">
          <div className="col-sm-8 mx-auto">
            <h1 className="text-center">ADD TICKET</h1>
          </div>
          <form
            encType="multipart/form-data"
            noValidate
            onSubmit={this.onSubmit}
          >
            <div className="form-group">
              <label htmlFor="name">Ticket Name</label>
              <input
                type="text"
                id="name"
                className="form-control"
                placeholder="Enter ticket name"
                value={this.state.name}
                onChange={this.onChange}
                name="name"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="content">Content</label>
              <textarea
                id="content"
                name="content"
                className="form-control"
                rows="2"
                placeholder="Enter content"
                value={this.state.content}
                onChange={this.onChange}
                required
              ></textarea>
            </div>
            <div className="form-group">
              <label htmlFor="messages">Messages</label>
              <textarea
                id="messages"
                className="form-control"
                name="messages"
                rows="2"
                placeholder="Enter Messages"
                value={this.state.messages.join(", ")} // Display messages as comma-separated string
                onChange={this.onChange}
                required
              ></textarea>
            </div>
            <div className="form-group">
              <label htmlFor="currentUser">Current User</label>
              <input
                type="text"
                id="currentUser"
                className="form-control"
                placeholder="Enter current user"
                value={this.state.currentUser}
                onChange={this.onChange}
                name="currentUser"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="createdBy">Created By</label>
              <input
                type="text"
                id="createdBy"
                className="form-control"
                placeholder="Enter creator's name"
                value={this.state.createdBy}
                onChange={this.onChange}
                name="createdBy"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="assignToUsers">Assign To Users</label>
              <input
                type="text"
                id="assignToUsers"
                className="form-control"
                placeholder="Enter users to assign, separated by commas"
                value={this.state.assignToUsers.join(", ")} // Display assigned users as comma-separated string
                onChange={this.onChange}
                name="assignToUsers"
              />
            </div>


            <div className="form-group">
            <label htmlFor="reportingManager">Assign To Users</label>
            <select
              className="form-control"
              name="assignToUsers"
              value={this.state.assignToUsers}
              onChange={this.onChange}
            >
              <option value="Self">Self</option>
              {this.state.users.map(user => (
                <option key={user._id} value={user.userName}>{user.userName}</option>
              ))}
            </select>
          </div>


            <div className="form-group">
              <label htmlFor="imageUpload">Upload Image</label>
              <input
                type="file"
                className="form-control-file"
                id="imageUpload"
                onChange={this._handleImageChange}
              />
              <small className="form-text text-muted">
                Upload an image for the ticket.
              </small>
            </div>
            <div className="form-group text-center">{$imagePreview}</div>
            <div className="form-group text-center">
              <button type="submit" className="btn btn-primary">
                Submit
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default Ticket;
