import React, { Component } from 'react';
import {
  Chart,
  ArcElement,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar, Pie } from 'react-chartjs-2';
import 'bootstrap/dist/css/bootstrap.min.css';

// Register the necessary components
Chart.register(ArcElement, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

class Landing extends Component {
  render() {
    // Data for the Pie Chart (ticket statuses)
    const pieData = {
      labels: ['Pending', 'Closed', 'Waiting'],
      datasets: [
        {
          data: [10, 20, 5], // Example data: replace with your own data
          backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
          hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
        },
      ],
    };

    // Data for the Bar Chart (tickets in a month)
    const barData = {
      labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'], // Example labels: replace with your own data
      datasets: [
        {
          label: 'Total Tickets',
          backgroundColor: 'rgba(75,192,192,0.4)',
          borderColor: 'rgba(75,192,192,1)',
          borderWidth: 1,
          hoverBackgroundColor: 'rgba(75,192,192,0.6)',
          hoverBorderColor: 'rgba(75,192,192,1)',
          data: [65, 59, 80, 81], // Example data: replace with your own data
        },
      ],
    };

    return (
      <div className="container">
        <div className="jumbotron mt-5">
          <div className="col-sm-12 mx-auto">
            <h1 className="text-center">WELCOME TO SUPPORT MANAGEMENT SYSTEM</h1>
          </div>
        </div>
        <div className="row mt-5">
          <div className="col-md-6 mb-4">
            <div className="card">
              <div className="card-body">
                <h2 className="text-center card-title">Ticket Statuses</h2>
                <Pie data={pieData} />
              </div>
            </div>
          </div>
          <div className="col-md-6 mb-4">
            <div className="card">
              <div className="card-body">
                <h2 className="text-center card-title">Total Tickets in a Month</h2>
                <Bar data={barData} />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Landing;
