import React, { Component, useState } from "react";
import jwt_decode from "jwt-decode";
import axios from "axios";
import Table from "@material-ui/core/Table";

import { Dropdown, Badge, Modal } from "react-bootstrap";
import { Button, Form } from "react-bootstrap";
import { ip } from "../services";
import axiosInstance from "../utils/axiosConfig";
import {
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
} from "@material-ui/core";
import moment from "moment";
class UserTicketShow extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
      filteredData: [],
      show: false,
      message: "",
      id: "",
      search: "",
      role: 0,
      userId: "",
    };
    this.handleChange = this.handleChange.bind(this);
  }

  getUsersTicket = (status) => {
    let url = `${ip}/tickets/usersTickets`;
    if (status !== undefined) {
      url += `?status=${status}`;
    }

    return axiosInstance
      .get(url)
      .then((response) => {
        this.setState({ data: response.data, filteredData: response.data });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  handleChange(e) {
    this.setState({ [e.target.name]: e.target.value });
    this.filterData(e.target.value);
  }

  handleClose = () => {
    this.setState({ show: false });
  };

  handleShow = (id) => {
    this.setState({ show: true, id: id });
  };

  handleAddMessage = (id, message) => {
    this.addMessage(id, message);
    this.setState({ show: false, message: "" });
    this.getUsersTicket();
  };
  changeStatus(id, status) {
    console.log("status", status);
    return axiosInstance
      .put(`${ip}/tickets/${id}`, {
        status,
      })
      .then((response) => {
        this.getUsersTicket();
        return response.data;
      })
      .catch((err) => {
        console.log(err);
      });
  }

  statusFilter(status) {
    this.getUsersTicket(status);
  }

  componentDidMount() {
    const token = localStorage.usertoken;
    const decoded = jwt_decode(token);
    this.getUsersTicket();
    this.setState({ role: decoded.role, userId: decoded._id });
  }

  addMessage(id, message) {
    return axiosInstance
      .post(`${ip}/tickets/addMessages`, { id, messages: message })
      .then((response) => {
        this.setState({ data: response.data });
      })
      .catch((err) => {
        console.log(err);
      });
  }

  filterData(status) {
    const filteredData = this.state.data.filter(
      (ticket) => ticket.status === status
    );
    this.setState({ filteredData });
  }

  statusName(status) {
    switch (status) {
      case 0:
        return (
          <Badge pill variant="primary">
            Opening
          </Badge>
        );
      case 1:
        return (
          <Badge pill variant="danger">
            Waiting
          </Badge>
        );
      case 2:
        return (
          <Badge pill variant="success">
            Closed
          </Badge>
        );
      default:
        return (
          <Badge pill variant="primary">
            Opening
          </Badge>
        );
    }
  }

  render() {
    return (
      <div className="container">
        <div className="jumbotron mt-5">
          <div className="col-sm-8 mx-auto">
            <h1 className="text-center">
              USER PAGE
              <br />
              TICKETS
            </h1>
          </div>
          <div className="d-flex justify-content-between align-items-center">
            <div className="mb-3">
              <Button
                variant="primary"
                className="mx-1"
                onClick={() => this.statusFilter(0)}
              >
                Opening
              </Button>
              <Button
                variant="danger"
                className="mx-1"
                onClick={() => this.statusFilter(1)}
              >
                Waiting
              </Button>
              <Button
                variant="success"
                className="mx-1"
                onClick={() => this.statusFilter(2)}
              >
                Closed
              </Button>
              <Button
                variant="dark"
                className="mx-1"
                onClick={() => this.getUsersTicket()}
              >
                ALL
              </Button>
            </div>
            <div className="col-xs-3 mb-3">
              <input
                className="form-control"
                style={{ float: "right" }}
                value={this.state.search}
                placeholder="Search TicketName"
                type="text"
                onChange={this.editSearchTerm}
              />
            </div>
          </div>
          <TableContainer component={Paper}>
            <Table size="small" aria-label="a dense table">
              <TableHead>
                <TableRow>
                  <TableCell>Ticket ID</TableCell>
                  <TableCell>Ticket Desc.</TableCell>
                  <TableCell>Created By</TableCell>
                  <TableCell align="right">Image</TableCell>
                  <TableCell align="right">Status</TableCell>
                  <TableCell align="right">Change Status</TableCell>
                  <TableCell align="right">Add Message</TableCell>
                  <TableCell align="right">Created TimeStamp</TableCell>
                  <TableCell align="right">Updated TimeStamp</TableCell>
                  <TableCell align="right">Closing TimeStamp</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {this.state.data.length > 0 &&
                  this.state.filteredData.map((row, index) => (
                    <TableRow key={index}>
                      <TableCell component="th" scope="row">
                        #{index + 1}
                      </TableCell>
                      <TableCell component="th" scope="row">
                        {row.name}
                      </TableCell>
                      <TableCell component="th" scope="row">
                        {row.createdBy.userName}
                      </TableCell>

                      <TableCell align="right">
                        <a
                          href={`http://localhost:5000/public/${row.img}`}
                          target="_parent"
                        >
                          <img
                            width="100px"
                            src={`http://localhost:5000/public/${row.img}`}
                            alt="ticket"
                          />
                        </a>
                      </TableCell>
                      <TableCell align="right">
                        {this.statusName(row.status)}
                      </TableCell>
                      <TableCell align="right">
                        <Dropdown>
                          <Dropdown.Toggle
                            size="sm"
                            variant="success"
                            id="dropdown-basic"
                          >
                            Change Status
                          </Dropdown.Toggle>
                          <Dropdown.Menu>
                            <Dropdown.Item
                              onClick={() => this.changeStatus(row._id, 0)}
                            >
                              Opening
                            </Dropdown.Item>
                            <Dropdown.Item
                              onClick={() => this.changeStatus(row._id, 1)}
                            >
                              Waiting
                            </Dropdown.Item>
                            <Dropdown.Item
                              onClick={() => this.changeStatus(row._id, 2)}
                            >
                              Closed
                            </Dropdown.Item>
                          </Dropdown.Menu>
                        </Dropdown>
                      </TableCell>
                      <TableCell align="right">
                        <i
                          className="fa-solid fa-comment-dots fa-2xl text-primary"
                          onClick={() => this.handleShow(row._id)}
                        ></i>
                      </TableCell>
                      <TableCell>
                        {moment(row.createdTimeStamp).fromNow()}
                      </TableCell>
                      <TableCell>
                        {moment(row.updatedTimeStamp).fromNow()}
                      </TableCell>
                      <TableCell>
                        {moment(row.closingTimeStamp).fromNow()}
                      </TableCell>
                    </TableRow>
                  ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Messages</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <section style="background-color: #eee;">
  <div class="container my-5 py-5">
    <div class="row d-flex justify-content-center">
      <div class="col-md-12 col-lg-10 col-xl-8">
        <div class="card">
          <div class="card-body">
            <div class="d-flex flex-start align-items-center">
              <img class="rounded-circle shadow-1-strong me-3"
                src="https://mdbcdn.b-cdn.net/img/Photos/Avatars/img%20(19).webp" alt="avatar" width="60"
                height="60" />
              <div>
                <h6 class="fw-bold text-primary mb-1">Lily Coleman</h6>
                <p class="text-muted small mb-0">
                  Shared publicly - Jan 2020
                </p>
              </div>
            </div>

            <p class="mt-3 mb-4 pb-2">
              Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
              tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam,
              quis nostrud exercitation ullamco laboris nisi ut aliquip consequat.
            </p>

            <div class="small d-flex justify-content-start">
              <a href="#!" class="d-flex align-items-center me-3">
                <i class="far fa-thumbs-up me-2"></i>
                <p class="mb-0">Like</p>
              </a>
              <a href="#!" class="d-flex align-items-center me-3">
                <i class="far fa-comment-dots me-2"></i>
                <p class="mb-0">Comment</p>
              </a>
              <a href="#!" class="d-flex align-items-center me-3">
                <i class="fas fa-share me-2"></i>
                <p class="mb-0">Share</p>
              </a>
            </div>
          </div>
          <div class="card-footer py-3 border-0" style={{"backgroundColor": "#f8f9fa"}}>
            <div class="d-flex flex-start w-100">
              <img class="rounded-circle shadow-1-strong me-3"
                src="https://mdbcdn.b-cdn.net/img/Photos/Avatars/img%20(19).webp" alt="avatar" width="40"
                height="40" />
              <div data-mdb-input-init class="form-outline w-100">
                <textarea class="form-control" id="textAreaExample" rows="4"
                  style={{background: "#fff"}}></textarea>
                <label class="form-label" for="textAreaExample">Message</label>
              </div>
            </div>
            <div class="float-end mt-2 pt-1">
              <button  type="button" data-mdb-button-init data-mdb-ripple-init class="btn btn-primary btn-sm">Post comment</button>
              <button  type="button" data-mdb-button-init data-mdb-ripple-init class="btn btn-outline-primary btn-sm">Cancel</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
              Close
            </Button>
            <Button
              variant="primary"
              onClick={() =>
                this.handleAddMessage(this.state.id, this.state.message)
              }
            >
              Add Message
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default UserTicketShow;
