import React, { Component, useState } from "react";
import jwt_decode from "jwt-decode";
import axios from "axios";
import Table from "@material-ui/core/Table";

import { Dropdown, Badge, Modal } from "react-bootstrap";
import { Button, Form } from "react-bootstrap";
import { ip } from "../services";
import axiosInstance from "../utils/axiosConfig";
import {
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
} from "@material-ui/core";
import moment from "moment";
class ShowTickets extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
      show: false,
      message: "",
      id: "",
      search: "",
      role: 0,
      userId: "",
    };
    this.handleChange = this.handleChange.bind(this);
  }

  getTicket = () => {
    return axiosInstance
      .get(`${ip}/tickets`)
      .then((response) => {
        this.setState({ data: response.data });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  getUsersTicket = () => {
    return axiosInstance
      .get(`${ip}/tickets`)
      .then((response) => {
        this.setState({ data: response.data });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  handleChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleClose = () => {
    this.setState({ show: false });
  };

  handleShow = (id) => {
    this.setState({ show: true, id: id });
  };

  handleAddMessage = (id, message) => {
    this.addMessage(id, message);
    this.setState({ show: false, message: "" });
    this.getTicket();
  };
  changeStatus(id, status) {
    console.log("status", status);
    return axiosInstance
      .put(`${ip}/tickets/${id}`, {
        status,
      })
      .then((response) => {
        this.getTicket();
        return response.data;
      })
      .catch((err) => {
        console.log(err);
      });
  }
  statusFilter(status) {
    return axios
      .post(`${ip}/tickets/filterStatus`, {
        status,
      })
      .then((response) => {
        this.setState({
          data: response.data,
        });

        return response.data;
      })
      .catch((err) => {
        console.log(err);
      });
  }
  componentDidMount() {
    const token = localStorage.usertoken;
    const decoded = jwt_decode(token);
    this.getTicket();
    this.setState({ role: decoded.role, userId: decoded._id });
  }

  addMessage(id, message) {
    return axiosInstance
      .post(`${ip}/tickets/addMessages`, { id, messages: message })
      .then((response) => {
        this.setState({ data: response.data });
      })
      .catch((err) => {
        console.log(err);
      });
  }

  statusName(status) {
    switch (status) {
      case 0:
        return (
          <Badge pill variant="primary">
            Opening
          </Badge>
        );
      case 1:
        return (
          <Badge pill variant="danger">
            Waiting
          </Badge>
        );
      case 2:
        return (
          <Badge pill variant="success">
            Closed
          </Badge>
        );
      default:
        return (
          <Badge pill variant="primary">
            Opening
          </Badge>
        );
    }
  }

  render() {
    const filteredData =
      this.state.role === 0
        ? this.state.data.filter(
            (ticket) => ticket.currentUser._id === this.state.userId
          )
        : this.state.data;

    return (
      <div className="container">
        {this.state.role === 1 ? (
          <div className="jumbotron mt-5">
            <div className="col-sm-8 mx-auto">
              <h1 className="text-center">
                ADMIN PAGE
                <br />
                TICKETS
              </h1>
            </div>
            <div className="d-flex justify-content-between align-items-center">
              <div className="mb-3">
                <Button
                  variant="primary"
                  className="mx-1"
                  onClick={() => this.statusFilter(0)}
                >
                  Opening
                </Button>
                <Button
                  variant="danger"
                  className="mx-1"
                  onClick={() => this.statusFilter(1)}
                >
                  Waiting
                </Button>
                <Button
                  variant="success"
                  className="mx-1"
                  onClick={() => this.statusFilter(2)}
                >
                  Closed
                </Button>
                <Button
                  variant="dark"
                  className="mx-1"
                  onClick={() => this.getTicket()}
                >
                  ALL
                </Button>
              </div>
              <div className="col-xs-3 mb-3">
                <input
                  className="form-control"
                  style={{ float: "right" }}
                  value={this.state.search}
                  placeholder="Search TicketName"
                  type="text"
                  onChange={this.editSearchTerm}
                />
              </div>
            </div>
            <TableContainer component={Paper}>
              <Table size="small" aria-label="a dense table">
                <TableHead>
                  <TableRow>
                    <TableCell>Ticket ID</TableCell>
                    <TableCell>Ticket Desc.</TableCell>
                    <TableCell>Current User</TableCell>
                    <TableCell>Created By</TableCell>
                    <TableCell>Assigned To</TableCell>
                    <TableCell align="right">Image</TableCell>
                    <TableCell align="right">Status</TableCell>
                    <TableCell align="right">Change Status</TableCell>
                    <TableCell align="right">Add Message</TableCell>
                    <TableCell align="right">Created TimeStamp</TableCell>
                    <TableCell align="right">Updated TimeStamp</TableCell>
                    <TableCell align="right">Closing TimeStamp</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {filteredData.length > 0 &&
                    filteredData.map((row, index) => (
                      <TableRow key={index}>
                        <TableCell component="th" scope="row">
                          {index + 1}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {row.name}
                        </TableCell>
                        <TableCell>{row.currentUser.userName}</TableCell>
                        <TableCell>{row.createdBy.userName}</TableCell>
                        <TableCell>
                          {row.assignToUsers.map((user, index) => (
                            <ul>
                              <li key={index}>{user.userName}</li>
                            </ul>
                          ))}
                        </TableCell>
                        <TableCell align="right">
                          <a
                            href={`http://localhost:5000/public/${row.img}`}
                            target="_parent"
                          >
                            <img
                              width="100px"
                              src={`http://localhost:5000/public/${row.img}`}
                              alt="ticket"
                            />
                          </a>
                        </TableCell>
                        <TableCell align="right">
                          {this.statusName(row.status)}
                        </TableCell>
                        <TableCell align="right">
                          <Dropdown>
                            <Dropdown.Toggle
                              size="sm"
                              variant="success"
                              id="dropdown-basic"
                            >
                              Change Status
                            </Dropdown.Toggle>
                            <Dropdown.Menu>
                              <Dropdown.Item
                                onClick={() => this.changeStatus(row._id, 0)}
                              >
                                Opening
                              </Dropdown.Item>
                              <Dropdown.Item
                                onClick={() => this.changeStatus(row._id, 1)}
                              >
                                Waiting
                              </Dropdown.Item>
                              <Dropdown.Item
                                onClick={() => this.changeStatus(row._id, 2)}
                              >
                                Closed
                              </Dropdown.Item>
                            </Dropdown.Menu>
                          </Dropdown>
                        </TableCell>
                        <TableCell align="right">
                          <i
                            className="fa-solid fa-comment-dots fa-2xl text-primary"
                            onClick={() => this.handleShow(row._id)}
                          ></i>
                        </TableCell>
                        <TableCell>
                          {moment(row.createdTimeStamp).fromNow()}
                        </TableCell>
                        <TableCell>
                          {moment(row.updatedTimeStamp).fromNow()}
                        </TableCell>
                        <TableCell>
                          {moment(row.closingTimeStamp).fromNow()}
                        </TableCell>
                      </TableRow>
                    ))}
                </TableBody>
              </Table>
            </TableContainer>
          </div>
        ) : (
          <div className="jumbotron mt-5">
            <div className="col-sm-8 mx-auto">
              <h1 className="text-center">
                USER PAGE
                <br />
                TICKETS
              </h1>
            </div>
            <TableContainer component={Paper}>
              <Table size="small" aria-label="a dense table">
                <TableHead>
                  <TableRow>
                    <TableCell>Ticket ID</TableCell>
                    <TableCell>Ticket Desc.</TableCell>

                    <TableCell align="right">Image</TableCell>
                    <TableCell align="right">Status</TableCell>
                    <TableCell align="right">Change Status</TableCell>
                    <TableCell align="right">Add Message</TableCell>
                    <TableCell align="right">Created TimeStamp</TableCell>
                    <TableCell align="right">Updated TimeStamp</TableCell>
                    <TableCell align="right">Closing TimeStamp</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {filteredData.length > 0 &&
                    filteredData.map((row, index) => (
                      <TableRow key={index}>
                        <TableCell component="th" scope="row">
                          {index + 1}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {row.name}
                        </TableCell>

                        <TableCell align="right">
                          <a
                            href={`http://localhost:5000/public/${row.img}`}
                            target="_parent"
                          >
                            <img
                              width="100px"
                              src={`http://localhost:5000/public/${row.img}`}
                              alt="ticket"
                            />
                          </a>
                        </TableCell>
                        <TableCell align="right">
                          {this.statusName(row.status)}
                        </TableCell>
                        <TableCell align="right">
                          <Dropdown>
                            <Dropdown.Toggle
                              size="sm"
                              variant="success"
                              id="dropdown-basic"
                            >
                              Change Status
                            </Dropdown.Toggle>
                            <Dropdown.Menu>
                              <Dropdown.Item
                                onClick={() => this.changeStatus(row._id, 0)}
                              >
                                Opening
                              </Dropdown.Item>
                              <Dropdown.Item
                                onClick={() => this.changeStatus(row._id, 1)}
                              >
                                Waiting
                              </Dropdown.Item>
                              <Dropdown.Item
                                onClick={() => this.changeStatus(row._id, 2)}
                              >
                                Closed
                              </Dropdown.Item>
                            </Dropdown.Menu>
                          </Dropdown>
                        </TableCell>
                        <TableCell align="right">
                          <i
                            className="fa-solid fa-comment-dots fa-2xl text-primary"
                            onClick={() => this.handleShow(row._id)}
                          ></i>
                        </TableCell>
                        <TableCell>
                          {moment(row.createdTimeStamp).fromNow()}
                        </TableCell>
                        <TableCell>
                          {moment(row.updatedTimeStamp).fromNow()}
                        </TableCell>
                        <TableCell>
                          {moment(row.closingTimeStamp).fromNow()}
                        </TableCell>
                      </TableRow>
                    ))}
                </TableBody>
              </Table>
            </TableContainer>
          </div>
        )}

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Messages</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div className="comments-section">
              {this.state.data
                .filter((ticket) => ticket._id === this.state.id)[0]
                ?.messages.map((message, index) => (
                  <div key={index} className="comment">
                    <div className="d-flex justify-content-between align-items-center">
                      <span>{message.text}</span>
                      <small>{moment(message.date).fromNow()}</small>
                    </div>
                    <hr />
                  </div>
                ))}
            </div>
            <Form className="mt-3">
              <Form.Group>
                <Form.Control
                  as="textarea"
                  rows={3}
                  placeholder="Add a messages..."
                  name="message"
                  value={this.state.message}
                  onChange={this.handleChange}
                  required
                />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
              Close
            </Button>
            <Button
              variant="primary"
              onClick={() =>
                this.handleAddMessage(this.state.id, this.state.message)
              }
            >
              Add Message
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default ShowTickets;
