import React, { Component } from 'react'
import { ticket } from './TicketFunctions'
import ImageUpload from './ImageUpload'
import axios from 'axios'

class Ticket extends Component {
  constructor() {
    super()
    this.state = {
      name: '',
      content: '',
      messages:'',
      status:'',
      img:'',
      file: '',
      imagePreviewUrl: '',
      errors: {}
    }
    
    this.onChange = this.onChange.bind(this)
    this.onSubmit = this.onSubmit.bind(this)
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }

  _handleSubmit(e) {
    e.preventDefault();
    // TODO: do something with -> this.state.file
    console.log('handle uploading-', this.state.file);
  }

  _handleImageChange(e) {
    e.preventDefault();

    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      this.setState({
        file: file,
        imagePreviewUrl: reader.result
      });
    }

    reader.readAsDataURL(file)
  }

  onSubmit(e) {
    e.preventDefault()

    const newTicket = {
      name: this.state.name,
      content: this.state.content,
      messages: this.state.messages,
      img: this.state.img
      //.split("fakepath\\")
      //.replace("C:\\fakepath\\", "")

    }
    console.log("this state file")
    console.log(this.state.file)
    return axios
    .post('tickets/addTickets', {
      name: this.state.name,
      img: this.state.file,
      content: this.state.content,
      messages: this.state.messages
    })
    .then(response => {
      console.log('New ticket success')
    })

  }


  onChangeHandler=event=>{

    console.log(event.target.files[0])

}

  render() {
    let {imagePreviewUrl} = this.state;
    let $imagePreview = null;
    if (imagePreviewUrl) {
      $imagePreview = (<img src={imagePreviewUrl} />);
    } else {
      $imagePreview = (<div className="previewText">Please select an Image for Preview</div>);
    }




    return (
      
      <div className="container">
            <div className="previewComponent">
          <form onSubmit={(e)=>this._handleSubmit(e)}>
            <input className="fileInput" 
              type="file" 
              onChange={(e)=>this._handleImageChange(e)} />
            <button className="submitButton" 
              type="submit" 
              onClick={(e)=>this._handleSubmit(e)}>Upload Image</button>
          </form>
         
        </div>
        <div className="jumbotron mt-5">
          <div className="col-sm-8 mx-auto">
            <h1 className="text-center">ADD TICKET</h1>
          </div>
          <form encType="multipart/form-data" noValidate onSubmit={this.onSubmit} >
          <div>
          <label htmlFor="name">Ticket name</label> 
          <input type="text" id="name" placeholder="Enter ticket name"
                  value={this.state.name}
                  onChange={this.onChange}  name="name" required ></input>
          </div>
          <div> 
                <label htmlFor="content">content</label> 
                <textarea id="content" name="content" rows="2" 
                          placeholder="Enter content"
                          value={this.state.content}
                          onChange={this.onChange}  required> 
                </textarea> 
           </div>  
           <div> 
                <label htmlFor="content">messages</label> 
                <textarea id="messages" name="messages" rows="2" 
                          placeholder="Enter Message"
                          value={this.state.messages.text}
                          onChange={this.onChange}  required> 
                </textarea> 
           </div>  
            <div> 
                <label htmlFor="img">Upload Image</label> 
                <input type="file"
                       name="img" 
                       placeholder="Upload İmage"
                  value={this.state.img}
                  
                  onChange={this.onChange} 
                       required></input>
            </div> 
            <div> 
                <button type="submit">Submit</button> 
            </div> 
          </form>
        </div>
      </div>
    )
  }
}

export default Ticket
