import React, { Component } from 'react'
import { ticket } from './TicketFunctions'
import {ImageUpload} from './ImageUpload'

class Ticket extends Component {
  constructor() {
    super()
    this.state = {
      name: '',
      content: '',
      messages:'',
      status:'',
      img:'',
      errors: {}
    }
    
    this.onChange = this.onChange.bind(this)
    this.onSubmit = this.onSubmit.bind(this)
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }

  

  onSubmit(e) {
    e.preventDefault()

    const newTicket = {
      name: this.state.name,
      content: this.state.content,
      messages: this.state.messages,
      img: this.state.img
      //.split("fakepath\\")
      //.replace("C:\\fakepath\\", "")

    }

    ticket(newTicket).then(res => {
      this.props.history.push(`/tickets`)
    })
  }


  onChangeHandler=event=>{

    console.log(event.target.files[0])

}

  render() {
    return (
      <div className="container">
        <ImageUpload/>
        <div className="jumbotron mt-5">
          <div className="col-sm-8 mx-auto">
            <h1 className="text-center">ADD TICKET</h1>
          </div>
          <form encType="multipart/form-data" noValidate onSubmit={this.onSubmit} >
          <div>
          <label htmlFor="name">Ticket name</label> 
          <input type="text" id="name" placeholder="Enter ticket name"
                  value={this.state.name}
                  onChange={this.onChange}  name="name" required ></input>
          </div>
          <div> 
                <label htmlFor="content">content</label> 
                <textarea id="content" name="content" rows="2" 
                          placeholder="Enter content"
                          value={this.state.content}
                          onChange={this.onChange}  required> 
                </textarea> 
           </div>  
           <div> 
                <label htmlFor="content">messages</label> 
                <textarea id="messages" name="messages" rows="2" 
                          placeholder="Enter Message"
                          value={this.state.messages.text}
                          onChange={this.onChange}  required> 
                </textarea> 
           </div>  
            <div> 
                <label htmlFor="img">Upload Image</label> 
                <input type="file"
                       name="img" 
                       placeholder="Upload İmage"
                  value={this.state.img}
                  
                  onChange={this.onChange} 
                       required></input>
            </div> 
            <div> 
                <button type="submit">Submit</button> 
            </div> 
          </form>
        </div>
      </div>
    )
  }
}

export default Ticket
